SET NAMES utf8;

CREATE DATABASE IF NOT EXISTS `iptv` /*!40100 DEFAULT CHARACTER SET utf8 */;
USE `iptv`;

DROP TABLE IF EXISTS `categories`;
CREATE TABLE IF NOT EXISTS `categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) NOT NULL,
  `pos` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `playlist`;
CREATE TABLE IF NOT EXISTS `playlist` (
  `ch_num` int(11) NOT NULL COMMENT 'Номер канала',
  `name` varchar(255) NOT NULL COMMENT 'Наименование канала',
  `url` varchar(255) NOT NULL COMMENT 'URL канала',
  `category_id` int(11) NOT NULL COMMENT 'id категории',
  `pos_in_category` int(11) NOT NULL COMMENT 'позиция в категории',
  `epg_id` int(11) DEFAULT NULL COMMENT 'Номер в базе EPG',
  `name_tr` varchar(255) NOT NULL COMMENT 'Имя канала в транслитерации',
  PRIMARY KEY (`ch_num`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

